var Mailgun = require('mailgun').Mailgun;

var setMailConfiguration = function (sender, receiver, content){
    var mg = new Mailgun('key-f876af9c5bea05c549abe55809d5802f');
    mg.sendRaw(sender,
        [receiver],
        'From: ' + sender +
        '\nTo: ' + receiver +
        '\nContent-Type: text/html; charset=utf-8' +
        '\nSubject: Rest Password -- TgLnk' + // <-- this line is important
        '\n\n ' + content,   // <----- this line is important too
        function(err) { err && console.log(err) });
};

exports.sendMail =function (sender, receiver, content){
    return  setMailConfiguration(sender, receiver, content);
}

