var nodemailer = require('nodemailer');
var transporter = nodemailer.createTransport({
    host: 'atom.corp.ebay.com',//atom.corp.ebay.com
    tls: {rejectUnauthorized: false},
    debug:true
});

exports.transporter = function () {
    return transporter;
};